{{ Skill progression max10 | title track = [[Sunspear rank]] | var1 name = Level | var1 at0 = 4 | var1 at10 = 14 | var2 name = [[Duration]] | var2 at0 = 75 | var2 at10 = 150 | var4 name = [[Heal]]ing | var4 at0 = 10 | var4 at10 = 20 }}
